#@title
# Importing the required Libraries
import pandas as pd
from sklearn.cluster import KMeans
import numpy as np
from scipy import stats
import seaborn as sns
import matplotlib.pyplot as plt
from matplotlib import rcParams
 
rcParams['figure.figsize'] = (8,6)

